// Valors per omissió
var workDays = [1, 2, 3, 4, 5]; // Dilluns a divendres; diumenge == 0, dissabte == 6
var workStart = [8, 30]; // Inici del dia laborable com a [H, M] (hores, minuts)
var workEnd = [17, 30]; // Finalització del dia laborable com a [H, M]
if (args &amp;&amp; args[0])
 workDays = args[0];
if (args &amp;&amp; args[1])
 workStart = args[1];
if (args &amp;&amp; args[2])
 workEnd = args[2];
if (prev)
 // No està pensat per utilitzar-se en un funcionament normal, sinó que es fa servir com a valor de data i hora actuals per fer proves.
 next = new Date(prev);
else
 next = new Date();
// Si ja hem sobrepassat l&#x27;hora de finalització del dia laborable o no ens trobem en un dia laborable, passar a l&#x27;inici
// del dia següent.
while ((next.getHours() &gt; workEnd[0]) ||
 (next.getHours() == workEnd[0] &amp;&amp; next.getMinutes() &gt; workEnd[1]) ||
 (workDays.indexOf(next.getDay()) == -1)) {
 next.setDate(next.getDate() + 1);
 next.setHours(workStart[0]);
 next.setMinutes(workStart[1]);
}
// Si ens trobem en una hora anterior a l&#x27;inici de la jornada laboral, anar al temps definit com a inici de la jornada.
if ((next.getHours() &lt; workStart[0]) ||
 (next.getHours() == workStart[0] &amp;&amp; next.getMinutes() &lt; workStart[1])) {
 next.setHours(workStart[0]);
 next.setMinutes(workStart[1]);
}

